(18, 'Bán đất nền sổ đỏ trung tâm thành phố Chí Linh, Hải Dương', '20201127173514-b839_wm.jpg,20201127173521-5a75_wm.jpg,20201127173528-d53a_wm.jpg,20201127173543-1b6a_wm.jpg,20201127173605-d4cb_wm.jpg', 'Hải Dương', 'Chí Linh, Hải Dương', 1.2, 100, '<p>&nbsp;</p>\r\n\r\n<p>Dự &aacute;n đẹp nhất th&agrave;nh phố Ch&iacute; Linh.<br />\r\nVị tr&iacute;: Trung t&acirc;m th&agrave;nh phố, Quảng Trường Trung T&acirc;m...<br />\r\nHạ tầng: Ho&agrave;n thiện đường trải nhựa 18m, c&ocirc;ng vi&ecirc;n c&acirc;y xanh...<br />\r\nPh&aacute;p l&yacute;: Sổ đỏ c&ocirc;ng trao tay, nhận ngay đất đẹp.<br />\r\nKết nối: Quốc Lộ 18, Quốc Lộ 37, đi Quảng Ninh, Bắc Ninh, bệnh viện, trường học, trung t&acirc;m h&agrave;nh ch&iacute;nh c&ocirc;ng...<br />\r\nDiện t&iacute;ch: 5x20m = 100m2/l&ocirc;.<br />\r\nGi&aacute;: 12tr/m2.</p>\r\n', '<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:500px\">\r\n	<tbody>\r\n		<tr>\r\n			<td>Loại tin đăng:</td>\r\n			<td>B&aacute;n đất nền dự &aacute;n (đất trong dự &aacute;n quy hoạch)</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Địa chỉ:</td>\r\n			<td>Ch&iacute; Linh, Hải Dương</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Mặt tiền:</td>\r\n			<td>5 m</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Đường v&agrave;o:</td>\r\n			<td>18 m</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Ph&aacute;p l&yacute;:</td>\r\n			<td>Đ&atilde; c&oacute; sổ</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n', '<p>Nguyễn Thị Lan - lan@gmail.com</p>\r\n', '031564862', 1, '2021-03-27 13:53:47', 1, 9, 1),
(19, 'BÁN ĐẤT KẾ BÊN TRƯỜNG ĐẠI HỌC QUỐC TẾ VIỆT ĐỨC GẦN CHỢ VÀ TRUNG TÂM THƯƠNG MẠI', '20210226072057-a5a1_wm.jpg,20210226072057-dd97_wm.jpg,20210226072058-5eea_wm.jpg,20210226072714-2a19_wm.jpg,20210226072714-4fc6_wm.jpg,20210226072739-9a14_wm.jpg,20210301090459-ab31.jpg', 'Bình Dương', 'Đường Vành Đai 4, Phường Hòa Lợi, Bến Cát, Bình Dương', 6.2, 300, '<p>Diện t&iacute;ch đất: 300m2, thổ cư 100%, d&acirc;n cư đ&ocirc;ng đ&uacute;c.<br />\r\n<br />\r\nVị tr&iacute; vị tr&iacute; qua tiềm năng. Đối diện l&agrave; trung t&acirc;m thương mại v&agrave; c&ocirc;ng vi&ecirc;n lớn nhất.<br />\r\n<br />\r\nMặt tiền đường V&agrave;nh Đai 4 lưu th&ocirc;ng từ Long An qua Củ Chi tới s&acirc;n bay Long Th&agrave;nh.<br />\r\n<br />\r\nVị tr&iacute; kh&ocirc;ng những kế b&ecirc;n trường đại học quốc tế Việt Đức v&agrave; c&ograve;n gần trường đại học B&igrave;nh Dương v&agrave; Khu c&ocirc;ng nghiệp c&ocirc;ng nghệ cao.</p>\r\n', '<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:500px\">\r\n	<tbody>\r\n		<tr>\r\n			<td>Loại tin đăng:</td>\r\n			<td>B&aacute;n đất</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Địa chỉ:</td>\r\n			<td>Đường V&agrave;nh Đai 4, Phường H&ograve;a Lợi, Bến C&aacute;t, B&igrave;nh Dương</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Đường v&agrave;o:</td>\r\n			<td>62 m</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n', '<p>Tiến&nbsp; - facebook: tiennam</p>\r\n', '098558 0878', 1, '2021-03-27 13:57:37', 1, 9, 0),
(20, 'Chính chủ bán lô đất tuyến 2, dự án khu đô thị Cựu Viên, 100m2, vị trí đắc địa, 3.1 tỷ', '20210323114255-78bc_wm.jpg,20210323114303-54db_wm.jpg,20210323114304-1d32_wm.jpg', 'Hải Phòng', 'Dự án Cựu Viên, Đường Lê Duẩn, Phường Bắc Sơn, Kiến An, Hải Phòng', 3.1, 100, '<p>- Ch&iacute;nh chủ b&aacute;n l&ocirc; đất tuyến 2, dự &aacute;n khu đ&ocirc; thị Cựu Vi&ecirc;n, Bắc Sơn, Kiến An, HP.<br />\r\n- DT: 100m2, rộng 5m, d&agrave;i 20m, đường rộng 18m.<br />\r\n- Hướng Đ&ocirc;ng Nam, vị tr&iacute; đắc địa.<br />\r\n- Gi&aacute; b&aacute;n: 3.1 tỷ.</p>\r\n', '<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:500px\">\r\n	<tbody>\r\n		<tr>\r\n			<td>Loại tin đăng:</td>\r\n			<td>B&aacute;n đất nền dự &aacute;n (đất trong dự &aacute;n quy hoạch)</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Đại chỉ:</td>\r\n			<td>Dự &aacute;n Cựu Vi&ecirc;n, Đường L&ecirc; Duẩn, Phường Bắc Sơn, Kiến An, Hải Ph&ograve;ng</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Hướng nh&agrave;:</td>\r\n			<td>\r\n			<p>Đ&ocirc;ng-Nam</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Ph&aacute;p l&yacute;:&nbsp;</td>\r\n			<td>C&oacute; sổ.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n', '<p>Li&ecirc;n hệ để biết th&ocirc;ng tin</p>\r\n', '0793.225.717', 0, '2021-03-27 14:00:24', 1, 2, 0),
(21, 'Chính chủ bán nhanh 80m2 đất sát KQH Thanh Lam, giá đầu tư', '20210322080318-18e2_wm.jpg,20210322080318-e1d4_wm.jpg,20210322080334-749a_wm.jpg,20210322080334-e34a_wm.jpg', 'Huế', 'Đường Nguyễn Viết Xuân, Phường Thủy Phương, Hương Thủy, Thừa Thiên Huế', 1.2, 80, '<p>Ch&iacute;nh chủ b&aacute;n lại l&ocirc; đất 80m2 Nguyễn Viết Xu&acirc;n cạnh c&aacute;nh đồng Thanh Lam - Huế.<br />\r\nDiện t&iacute;ch 80m2 ngang 9m.<br />\r\nHướng T&acirc;y Bắc.<br />\r\nĐất đẹp vu&ocirc;ng vắn, đường rộng 4m oto đi lại thoải m&aacute;i, dễ d&agrave;ng.<br />\r\nNằm gần c&aacute;nh đồng Thanh Lam, c&aacute;ch trung t&acirc;m TP 7ph&uacute;t chạy xe.<br />\r\nNằm trong khu d&acirc;n cư y&ecirc;n tĩnh, tho&aacute;ng m&aacute;t, h&agrave;ng x&oacute;m văn minh, th&acirc;n thiện.<br />\r\nSổ đỏ ch&iacute;nh chủ - Ph&aacute;p l&yacute; r&otilde; r&agrave;ng - Hỗ trợ vay vốn ng&acirc;n h&agrave;ng.<br />\r\nRất đẹp để an cư v&agrave; đầu tư.</p>\r\n', '<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:500px\">\r\n	<tbody>\r\n		<tr>\r\n			<td>Loại đăng tin:&nbsp;</td>\r\n			<td>B&aacute;n đất</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Đại chỉ</td>\r\n			<td>Đường Nguyễn Viết Xu&acirc;n, Phường Thủy Phương, Hương Thủy, Thừa Thi&ecirc;n Huế</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Mặt tiền:&nbsp;</td>\r\n			<td>9</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Đường v&agrave;o:</td>\r\n			<td>4</td>\r\n		</tr>\r\n		<tr>\r\n			<td><br />\r\n			Hướng nh&agrave;:</td>\r\n			<td>T&acirc;y-Bắc</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Ph&aacute;p l&yacute;:</td>\r\n			<td>Đ&atilde; c&oacute;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n', '<p>Th&ocirc;ng tin sau khi li&ecirc;n hệ</p>\r\n', '4568131465', 0, '2021-03-27 14:03:27', 1, 2, 1),
(22, 'CĂN HỘ MINI CHO THUÊ TẠI AN DƯƠNG VƯƠNG, P4, Q5 GIÁ TỪ 5,5 TR/TH. GẦN TRƯỜNG ĐH, CÓ THANG MÁY', '20210320151759-229e_wm.jpg,20210320151759-7000_wm.jpg,20210320151759-edc9_wm.jpg,20210320151800-3c93_wm.jpg,20210320151800-3253_wm.jpg,20210320151806-365e_wm.jpg', 'Hồ Chí Minh', 'Đường An Dương Vương, Phường 4, Quận 5, Hồ Chí Minh', 0.5, 20, '<p>Căn hộ dịch vụ cho thu&ecirc; mới khai trương tại An Dương Vương, P4, Q5 gi&aacute; chỉ từ 5,5 triệu/th. Ưu ti&ecirc;n d&agrave;nh slot cho c&aacute;c bạn Sinh vi&ecirc;n Trường ĐH S&agrave;i G&ograve;n, ĐH Kinh tế cơ sở B, ĐH Khoa học tự nhi&ecirc;n, ĐH H&ugrave;ng Vương, ĐH Quốc Gia TP. HCM, ĐH Quốc tế Hồng B&agrave;ng, ĐH Sư phạm thể dục thể thao, ĐH Sư phạm, ĐH Y Dược, Cao Đẳng kinh tế đối ngoại, ĐH C&ocirc;ng nghệ.<br />\r\n<br />\r\nFree: Tặng miễn ph&iacute; truyền h&igrave;nh c&aacute;p, thang m&aacute;y, bếp chung v&agrave; 3 m&aacute;y giặt, m&aacute;y lọc nước tinh khiết.<br />\r\n<br />\r\nNh&agrave; mới n&ecirc;n gi&aacute; mềm chỉ trong th&aacute;ng 3/2021 n&agrave;y.<br />\r\nHĐ 1 năm c&ograve;n đc giảm th&ecirc;m gi&aacute; ph&ograve;ng.<br />\r\nPh&ograve;ng full nội thất, gần c&aacute;c trường ĐH Q5.<br />\r\nPh&ograve;ng 5,5tr/th 20m2.<br />\r\nPh&ograve;ng 7tr/th 25m2 c&oacute; ban c&ocirc;ng lớn.<br />\r\nK&iacute; hợp đồng 1 năm c&ograve;n được giảm gi&aacute; th&ecirc;m.</p>\r\n', '<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:500px\">\r\n	<tbody>\r\n		<tr>\r\n			<td>Loại tin đăng:</td>\r\n			<td>Cho thu&ecirc; nh&agrave; trọ, ph&ograve;ng trọ</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Địa chỉ:</td>\r\n			<td>Đường An Dương Vương, Phường 4, Quận 5, Hồ Ch&iacute; Minh</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n', '<p>Li&ecirc;n hệ th&ecirc;m để lấy th&ocirc;ng tin</p>\r\n', '0.3333.49421', 1, '2021-03-27 14:07:31', 0, 3, 1);

